mapper.select:
select *
from dt_equipment_info
where 1=1

    and id = #{equipment_info.id}


    and parent_node_code = #{equipment_info.parent_node_code}


    and equipment_code = #{equipment_info.equipment_code}


    and equipment_name = #{equipment_info.equipment_name}


    and equipment_type = #{equipment_info.equipment_type}


    and equipment_property_template_code = #{equipment_info.equipment_property_template_code}


    and equipment_property_code = #{equipment_info.equipment_property_code}


    and keyword = #{equipment_info.keyword}


    and data_val = #{equipment_info.data_val}


    and alarm_val = #{equipment_info.alarm_val}


    and control_val = #{equipment_info.control_val}

order by id
