mapper.select:
select *
from dt_monitor_property_template
where 1=1

    and id = #{monitorPropertyTemplate.id}


    and equipment_type = #{monitorPropertyTemplate.equipmentType}


    and equipment_property_template_code = #{monitorPropertyTemplate.equipmentPropertyTemplateCode}


    and equipment_property_template_name = #{monitorPropertyTemplate.equipmentPropertyTemplateName}


    and equipment_property_template_description =
    #{monitorPropertyTemplate.equipmentPropertyTemplateDescription}

order by id
