mapper.select:
select *
from dt_monitor_property_template
where 1=1

    and id = #{monitor_property_template.id}


    and equipment_type = #{monitor_property_template.equipment_type}


    and equipment_property_template_code = #{monitor_property_template.equipment_property_template_code}


    and equipment_property_template_name = #{monitor_property_template.equipment_property_template_name}


    and equipment_property_template_description =
    #{monitor_property_template.equipment_property_template_description}

order by id
